<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN"><html><head><title>Favorite Chicago Sounds &mdash; Admin</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"><link href="../css/mainstyle.css" rel="stylesheet" type="text/css"><link rel="icon" href="../images/yfcs_icon3.ico" type="image/x-icon" /> <link rel="shortcut icon" href="../images/yfcs_icon3.ico" type="image/x-icon" /><script src="../scripts/utils.js" language="javascript" type="text/javascript"></script></head><body bgcolor="#FFFFFF" text="000000" link="#0000FF" vlink="2d3f6f" alink="#0000FF" leftmargin="3" topmargin="3" marginwidth="3" marginheight="3" class="bodycopy" onLoad="MM_preloadImages('images/topmenu_02-down.gif','images/topmenu_02-over.gif','images/topmenu_03-over.gif','images/topmenu_04-down.gif','images/topmenu_04-over.gif','images/topmenu_05-down.gif','images/topmenu_05-over.gif','images/topmenu_06-down.gif','images/topmenu_06-over.gif','images/topmenu_07-down.gif','images/topmenu_07-over.gif','images/topmenu_03.gif')"><div align="center">	<table id="Table_01" width="531"  border="0" cellpadding="0" cellspacing="0">		<tr> 			<td valign="top"> <a href="index.php"><img src="../images/topmenu_01.gif" alt="" width="531" border="0" align="top"></a></td>		</tr>	</table>	<table border="1" cellspacing="0" cellpadding="5" width="100%">	<tr>		<td class="bodycopy">Favorite Sound</td>		<td class="bodycopy">Why?</td>		<td class="bodycopy">Address</td>		<td class="bodycopy">Name</td>		<td class="bodycopy">Neighborhood</td>		<td class="bodycopy">Email</td>		<td class="bodycopy">Use Name on Site</td>		<td class="bodycopy">Send Updates</td>	</tr>	<?php		require_once('../include/mysql_connect.php');	$query = "SELECT favorite_sounds.favSound, favorite_sounds.whySound, 		favorite_sounds.address, favorite_sounds.fname, favorite_sounds.lname, 		favorite_sounds.email, favorite_sounds.usename, favorite_sounds.updates, 		neighborhoods.name as neighborhood 		FROM favorite_sounds LEFT JOIN neighborhoods ON favorite_sounds.neighborhood_id = neighborhoods.id		ORDER BY dateAdded DESC";	$result = mysql_query($query);	$num=mysql_numrows($result);		mysql_close();		$i = 0;	while ($i < $num) {		$favSound = mysql_result($result,$i,"favSound");		$whySound = mysql_result($result,$i,"whysound");		$address = mysql_result($result,$i,"address");		$fname = mysql_result($result,$i,"fname");		$lname = mysql_result($result,$i,"lname");		$fullname = $fname . " " . $lname;		$neighborhood = mysql_result($result,$i,"neighborhood");		$email = mysql_result($result,$i,"email");		$usename = mysql_result($result,$i,"usename");		$updates = mysql_result($result,$i,"updates");		?>		<tr>			<td class="bodycopy"><? echo $favSound; ?></td>			<td class="bodycopy"><? echo $whySound; ?></td>			<td class="bodycopy"><? echo $address; ?></td>			<td class="bodycopy"><? echo $fullname; ?></td>			<td class="bodycopy"><? echo $neighborhood; ?></td>			<td class="bodycopy"><? echo $email; ?></td>			<td class="bodycopy"><? echo $usename; ?></td>			<td class="bodycopy"><? echo $updates; ?></td>		</tr>			<?		$i++;	}	?>	</table></div></body></html>